      image_tag='<img src="'+image_url.replace('/s72-c/','/s'+image_size+'-c/')+'" alt="'+post_title+'"/>';
      yimage_tag = '<img src="' + image_url.replace('default', '0') + '" alt="' + post_title + '"/>';
